import './index.scss'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCss3, faGithub, faHtml5, faJsSquare, faReact, faSass } from '@fortawesome/free-brands-svg-icons'
import { useParams, useLocation } from "react-router-dom";

const skillIcons = {
    HTML: faHtml5,
    CSS: faCss3,
    JAVASCRIPT: faJsSquare,
    SASS: faSass,
    REACT: faReact
  };

const MoreProjects = () => {
    const { id } = useParams();
    const location = useLocation();
    const project = location.state?.project;

    if (!project){
        return <div className="project-empty"><h2>Projeto n√£o encontrado</h2></div>
    } else {
        return (
            <div key={id} className="more-projects">
                <div className="p-header">
                    <h1>{project.name}</h1>
                    <a href={project.link}>{project.linkName}</a>
                </div>
                <div className="p-content">
                    <img src={project.image} alt={project.name} />
                    <div className="p-description">
                        <div>{project.description}</div>
                        <a href={project.github}> <FontAwesomeIcon icon={faGithub} /> Github</a>
                        <div className="skills-gallery">
                            {project.skills.map(({name}) => (
                                <span><FontAwesomeIcon icon={skillIcons[name]} color="#022c43"/> {name}</span>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        );
    }

}

export default MoreProjects;
